import React from "react";

class AdminPanel extends React.Component {

  constructor() {
    super();
    this.state ={
      book :{
        name : "",
        author: "",


      }
    };

  };

  handleChange = (event) => {

   let newBook = {
         [event.target.name]: event.target.value
    }
   this.setState({
     book: newBook
   })

  }



  render() {
    const checkboxColor = {
      color: "white"
    };

    return (
      <div className="adminpanel col-md-4">
        <form>
          <div className="form-group">
            <input
              type="text"
              placeholder="Nazwa ksiazki"
              id="name"
              name="name"
              className="form-control"

            />
          </div>
          <div className="form-group">
            <input
              type="text"
              placeholder="Autor"
              id="author"
              name="author"
              className="form-control"
            />
          </div>
          <div className="form-group">
            <textarea
              placeholder="Opis książki"
              id="description"
              name="description"
              className="form-control"
            />
          </div>
          <div className="form-group">
            <label
              htmlFor="bookOnStock"
              className="form-check-label"
              style={checkboxColor}
            >
              Dostępność:{" "}
            </label>
            <input
              type="checkbox"
              placeholder="Dostęność książki"
              id="bookOnStock"
              name="bookOnStock"
              className="form-check"
            />
          </div>
          <div className="form-group">
            <textarea
              placeholder="Grafika"
              id="image"
              name="image"
              className="form-control"
            />
          </div>
          <button type="submit" className="btn btn-success">
            Dodaj
          </button>
          </form>
      </div>
    );
  }
}

export default AdminPanel;
