import React from "react";
import BookView from "./BookView";
import { fbase } from "../fbase";
import { confirmAlert } from 'react-confirm-alert';

class Inventory extends React.Component {
  constructor() {
    super();
    this.state = {
        books:[]
    };
  }



  componentDidMount() {
    this.ref = fbase.syncState("bookstore/books", {
      context: this,
      state: "books"
    });
  }

  componentWillUnmount() {
    fbase.removeBinding(this.ref);
  }


  delete = title => {   
    confirmAlert(ask) 
    this.setState({ 
    books :this.state.books.filter(book => title !== book.name)
    }) 

  }

  render() {
    let bookList = <h4> Brak danych</h4>;

    if (Array.isArray(this.state.books)) {
      bookList = this.state.books.map(book => {
        return <BookView key={book.name} book={book} addToOrder={this.props.addToOrder} delete={this.delete}/>;
      });     
    }

    return (
      <div className="inventory col-md-6">
        <div className="list_title"> Lista książek: </div>
        {bookList}
      </div>
    );

  }
}

const ask = {
  title: 'Title',
  message: 'Message',
  buttons: [
    {
      label: 'Yes',
      onClick: () => alert('Click Yes')
    },
    {
      label: 'No',
      onClick: () => alert('Click No')
    }
  ],
  childrenElement: () => <div />,
  customUI: ({ title, message, onClose }) => <div>Custom UI</div>,
  willUnmount: () => {}
}
export default Inventory;
