import React from 'react';

class Header extends React.Component {

    constructor() {
        super();
        this.state = {
            bookstoreName : "Black Books",
            clicked: true,
            textColor: "white",
            backgroundColor: "black"
        }
    }

    handleClick =() =>{

        if(this.state.clicked){
            this.setState({
                bookstoreName: "White Books"                
            })
        } else {
            this.setState({
                bookstoreName: "Black Books"              
            })
        }

             this.setState({
            clicked : !this.state.clicked
        })
    }

    render () {
        return (
        <div className="row header" style="">
        <button onClick={this.handleClick} className="btn btn-success" >Klik</button><h1>{this.state.bookstoreName}</h1>
        </div>
        );
    }

}

export default Header;
