import React from 'react';
import { Link } from 'react-router-dom';
import {connect} from 'react-redux';

 class AddBookForm extends React.Component {
  constructor() {
    super();
    this.state = {
      book: {
        name: "",
        author:"",
        description: "",
        image:"",
        onStock: true,
        price:""
      }
    };
  }

  handleChange = event => {
    let newBook;


    if (event.target.name === "onStock") {
      newBook = {
        ...this.props.book,
        [event.target.name]: event.target.checked
      };
    } else {
      newBook = {
        ...this.props.book,
        [event.target.name]: event.target.value
      };
    }
    this.props.updateBook(newBook)
  };

  addNewBook = event => {
    event.preventDefault();


    if (!this.props.editMode) {

      const newBook = {...this.props.book}
      this.props.addNewBook(newBook)
    } else {
      this.setState({ books: [newBook] });
    }

    this.setSprops.updateBook ({
      book: {
        name: "",
        author: "", 
        description: "",
        onStock: true,
        image: ""
      }
    });
  };



  render() {
    const label = this.props.editMode ? "Edytuj" : "Dodaj";

    return (
      <React.Fragment>
        <div className="adminpanel col-md-4">
          <form onSubmit={this.props.addNewBook}>
            <div className="form-group">
              <input
                type="text"
                placeholder="Nazwa ksiazki"
                id="name"
                name="name"
                className="form-control"
                onChange={this.handleChange}
                value={this.props.book.name}
              />
            </div>
            <div className="form-group">
              <input
                type="text"
                placeholder="Gatunek"
                id="genre"
                name="genre"
                className="form-control"
                onChange={this.handleChange}
                value={this.props.book.genre}
              />
            </div>
            <div className="form-group">
              <input
                type="text"
                placeholder="Autor"
                id="author"
                name="author"
                className="form-control"
                onChange={this.handleChange}
                value={this.props.book.author}
              />
            </div>
            <div className="form-group">
              <input
                type="text"
                placeholder="Cena"
                id="price"
                name="price"
                className="form-control"
                onChange={this.handleChange}
                value={this.props.book.price}
              />
            </div>
            <div className="form-group">
              <textarea
                placeholder="Opis książki"
                id="description"
                name="description"
                className="form-control"
                onChange={this.handleChange}
                value={
                  this.props.book.description
                }
              />
            </div>
            <div className="form-group">
              <label htmlFor="onStock" className="form-check-label">
                Dostępność:
              </label>

              <input
                type="checkbox"
                id="onStock"
                name="onStock"
                className="form-check"
                onChange={this.handleChange}
                value={this.props.book.onStock}
              />
            </div>
            <div className="form-group">
              <textarea
                placeholder="Grafika"
                id="image"
                name="image"
                className="form-control"
                onChange={this.handleChange}
                value={this.props.book.image}
              />
            </div>
            <button type="submit" className="btn btn-success">
              {label}
            </button>
          </form>
          <button className="btn btn-sm btn-danger" onClick={this.props.logout}>
            Wyloguj
          </button>
          <Link to="/">
            <button className="btn btn-block btn-info">
              <i className="fa fa-home">Powrót</i>
            </button>
          </Link>
        </div>
      </React.Fragment>
    );
  }
}

const mapStateToProps = state => {
  return {
    book: state.book,

  }
}

const mapDispatchToProps = dispatch => {
  return {
      updateBook: book => dispatch({type: "updateBook", payload: book})
  }
}


const BookForm = connect(mapStateToProps,mapDispatchToProps)(AddBookForm);

export default BookForm;
